package java4;

public class Coke {
	private int price;
	private String desc;

	public int getPrice() {
		return price;
	}

	public Coke() {
		// TODO Auto-generated constructor stub
	}
	
	public Coke(int price, String desc) {
		this.price = price;
		this.desc = desc;
	}

	public void setPrice(int price) {
		this.price = price;
	}

	public String getDesc() {
		return desc;
	}

	public void setDesc(String desc) {
		this.desc = desc;
	}

}
